     */
    @GetMapping("/search")
    @Operation(summary = "Search data sources", description = "Search data sources by name (case-insensitive)")
    @ApiResponse(responseCode = "200", description = "Search completed successfully")
    public ResponseEntity<List<DataSourceConfig>> searchDataSources(
            @Parameter(description = "Search query") @RequestParam @NotBlank String q) {

        logger.debug("Searching data sources with query: {}", q);

        List<DataSourceConfig> dataSources = dataSourceService.searchDataSourcesByName(q);
        return ResponseEntity.ok(dataSources);
    }

    /**
     * Get data source statistics
     */
    @GetMapping("/statistics")
    @Operation(summary = "Get data source statistics", description = "Retrieve statistics about data sources")
    @ApiResponse(responseCode = "200", description = "Statistics retrieved successfully")
    public ResponseEntity<com.dataprofiler.dto.DataSourceStatistics> getDataSourceStatistics() {
        logger.debug("Retrieving data source statistics");

        com.dataprofiler.dto.DataSourceStatistics stats = dataSourceService.getDataSourceStatistics();
        return ResponseEntity.ok(stats);
    }
